document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".dropdown-toggle").forEach(function(e){e.addEventListener("click",function(){var e=this.getAttribute("id").replace("Button","");let o=document.getElementById(e);e=this.querySelector(".arrow");document.querySelectorAll(".dropdown-menu").forEach(function(e){e!==o&&e.classList.contains("show")&&(e.classList.remove("show"),document.querySelectorAll(".mbird-filter .arrow").forEach(e=>{e.classList.remove("open")}))}),o.classList.toggle("show"),e.classList.toggle("open")})}),document.addEventListener("click",function(e){e.target.matches(".dropdown-toggle")||document.querySelectorAll(".dropdown-menu").forEach(function(e){e.classList.contains("show")&&(e.classList.remove("show"),document.querySelectorAll(".mbird-filter .arrow").forEach(e=>{e.classList.remove("open")}))})}),document.querySelectorAll(".filter-checkbox").forEach(function(e){e.addEventListener("click",function(e){e.stopPropagation()})}),document.querySelectorAll(".dropdown-item label").forEach(function(e){e.addEventListener("click",function(e){e.stopPropagation()})})});